function fullImageIcon(){
var inSrc = 'resource/img/in-full.svg';
var outSrc = 'resource/img/out-full.svg';
var curSrc = $("#fully").attr('src');
  if ( curSrc === inSrc ) {
      $("#fully").attr('src', outSrc);
  }
  if ( curSrc === outSrc ) {
      $("#fully").attr('src', inSrc);
  }
}
function fullscreen() {


    if ((document.fullScreenElement && document.fullScreenElement !== null) ||
        (!document.mozFullScreen && !document.webkitIsFullScreen)) {
	
        if (document.documentElement.requestFullScreen) {
            document.documentElement.requestFullScreen();
        } else if (document.documentElement.mozRequestFullScreen) {
            document.documentElement.mozRequestFullScreen();
        } else if (document.documentElement.webkitRequestFullScreen) {
            document.documentElement.webkitRequestFullScreen(Element.ALLOW_KEYBOARD_INPUT);
        }
    } else {

        if (document.cancelFullScreen) {
            document.cancelFullScreen();
        } else if (document.mozCancelFullScreen) {
            document.mozCancelFullScreen();
        } else if (document.webkitCancelFullScreen) {
            document.webkitCancelFullScreen();
        }
    }
}

document.addEventListener('fullscreenchange', FSHandler);
document.addEventListener('webkitfullscreenchange', FSHandler);
document.addEventListener('mozfullscreenchange', FSHandler);
document.addEventListener('MSFullscreenChange', FSHandler);

function FSHandler() {

	// FS = FullScreen
   // if (!document.fullscreenElement 
	//&& !document.webkitIsFullScreen 
	//&& !document.mozFullScreen 
	//&& !document.msFullscreenElement) { } else {}
	fullImageIcon();
// future 2: The key shortcut: "press F to go in fullscreenmode"
}
